    private void blogActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_blogActionPerformed
        // TODO add your handling code here:
        try {
            Statement stat= conn.createStatement();
            String sql = "Select * from login where username='"+tuser.getText()+
            "' And password='"+tpass.getText()+"'";
            ResultSet rs = stat.executeQuery(sql);
            if (rs.next())
            {
                String user = rs.getString("username");
                String pass = rs.getString("password");
                if (pass.equals(pass) && user.equals(user)){
                    JOptionPane.showMessageDialog(this, "Login Berhasil");
                    {
                        new menutama().setVisible(true);
                        dispose();
                    }
                }
            
            }
            else
            JOptionPane.showMessageDialog(this, "Masukan Username dan Password dengan benar");
            
        }
            catch (Exception e) {
            JOptionPane.showMessageDialog(null, "Username Tidak Ada");
        
    }                  
    }//GEN-LAST:event_blogActionPerformed

